function rbswWidgetInstance(widgetNode){
    this.instanceId = widgetNode.getAttribute("rbsw-widget-id");

    this.headerNode = widgetNode.querySelector(".rbsw-header");
	this.footerNode = widgetNode.querySelector(".rbsw-footer");
    this.contentNode = widgetNode.querySelector(".rbsw-content");

    this.modalContainerNode = widgetNode.querySelector(".rbsw-modal-container");
	this.settingsModalNode = widgetNode.querySelector(".rbsw-settings-modal");
    this.itemModalNode = widgetNode.querySelector(".rbsw-item-modal");

	this.content = new rbswNaviWidgetContent(this);
    this.header = new rbswNaviWidgetHeader(this);
	this.footer = new rbswNaviWidgetFooter(this);
	this.itemModal = new rbswNaviWidgetItemMod(this);

	console.log(this);
	/*
    this.settingsModal = new rbswWidgetInstanceSettingsModal(this);
	*/
}
