sys_ui_script.script:
/***FOOTER***/
function rbswNaviWidgetFooter(widgetInstance){
	var wg = this.wg = widgetInstance;

	this.settingsBtnNode = wg.footerNode.querySelector(".rbsw-footer-settings-btn");
	this.settingsBtnNode.addEventListener("click", function(){
		wg.settingsModal.show();
	});

	this.newItemBtnNode =  wg.footerNode.querySelector(".rbsw-footer-new-item-btn");
	this.newItemBtnNode.addEventListener("click", function(){
		wg.itemModal.show();
	});
}
