function rbswNaviWidgetContent(widgetInstance){
	var wg = this.wg = widgetInstance;

	this.listNode = wg.contentNode.querySelector(".rbsw-content-list");
	this.listItems = this.listNode.querySelectorAll(".rbsw-content-list-item");
	this.lastItemIdx = this.listItems.length;

	this.listItems.forEach(function(listItem){
		var item = new rbswNaviWidgetListItem(listItem);
			item.initialize();
			item.addListeners();
	});
}

